sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (newValue === '') {
		return;
	}

	var reqType = g_form.getValue('u_request_type');

	var whatNeededOptions = {"hr" : [["hr1","Human Resources 1"],["hr2","Human Resources 2"],['other','Other']],
		'legal' : [["legal1","Legal 1"],["legal2","Legal 2"],['other','Other']],
		'facilities' : [["facilities1","Facilities 1"],["facilities2","Facilities 2"],['other','Other']]};


		var optionName = 'u_what_needed';
		var whatNeeded = g_form.getValue(optionName);
		g_form.clearOptions(optionName);

		var options = whatNeededOptions[reqType];

//alert(options);
		var option;
		for(var i=0,len=options.length; i < len; i++){
			option = options[i];
			g_form.addOption(optionName, option[0], option[1]);
		}

		if(isLoading && !g_form.isNewRecord()){
			jslog('What Needed : ' + whatNeeded);
			g_form.setValue(optionName,whatNeeded);
		}


	}
